import React, { useState } from "react";
import "./../../templates/css/ProductCard.css";

export default function ProductCard({ product, onBuy }) {
  const { image, name, manufacturer, price, bulk_discount, stock } = product;
  const [qty, setQty] = useState(1);
  const [error, setError] = useState("");
const [maxQty, setMaxQty] = useState(stock)


  const handleInputChange = (e) => {
    const value = e.target.value;

    if (value === "" || (Number(value) > 0 && Number.isInteger(Number(value)))) {
      setQty(value);
      setError("");
    } else {
      setError("Please enter a valid positive number.");
    }
  };

  const handleBuyClick = () => {
    const quantity = parseInt(qty);
    if (!quantity || quantity <= 0) {
      setError("Quantity must be at least 1.");
      return;
    }
    onBuy(name, quantity);
    setQty("");
  };

  return (
    <div className="product-card">
  {bulk_discount && (
    <div className="discount-badge">
      -{bulk_discount.discount_percent}%
    </div>
  )}
  <img src={image} alt={name} className="product-image" />
  <div className="product-info">
    <h3>{name}</h3>
    <p className="manufacturer">{manufacturer || "unknown"}</p>
    <p className="price">${price.toFixed(2)}</p>
    {bulk_discount && (
      <p className="discount-text">
        Buy {bulk_discount.min_qty}+ for ${(price * (1 - bulk_discount.discount_percent/100)).toFixed(2)} each
      </p>
    )}
  </div>
  <div className="buy-section">
    <input
      type="number"
      min="1"
      max={stock}
      placeholder="Qty"
      value={qty || 1}
      className="qty-input"
      onChange={handleInputChange}
    />
  </div>
  {error && <div className="error">{error}</div>}
    <button className="btn-buy" onClick={handleBuyClick}>
      Add to Cart
    </button>
</div>
  );
}
