import React, { useState } from "react";
import "../../templates/css/CartItemCard.css";

function CartItemCard({ item, onQuantityChange, onRemove }) {
  const [quantity, setQuantity] = useState(item.units);

  const handleInputChange = (e) => {
    const newQty = parseInt(e.target.value) || 0;
    setQuantity(newQty);
    const diff = newQty - item.units;
    if (diff !== 0) {
      onQuantityChange(item.name, diff);
    }
  };

  return (
    <div className="cart-item-card">
      <img src={item.image} alt={item.name} />
      <div>
        <h3>{item.name}</h3>
        <p>Price: {item.price}â‚¬</p>
        <input
          type="number"
          min="0"
          value={quantity}
          onChange={handleInputChange}
        />
        <button onClick={() => onRemove(item.name)}>Remove</button>
      </div>
    </div>
  );
}
